mapper.select:
SELECT TS.STUDY_ID AS studyId, TS.PRTCPNT_ID AS prtcpntId, TS.SUMRY_SEQ AS sumrySeq, TS.AE_NO AS aeNo, TS.MEDDRA_CODE AS medDraCode, TS.AE_TERM_CTCAE AS aeTermCtcae, TS.AE_TERM_OTHER AS aeTermOther, TS.AE_GRADE AS aeGrade, DATE_FORMAT(TS.AE_STRT_DE, '%Y-%m-%d') AS aeStrtDe, DATE_FORMAT(TS.AE_END_DE, '%Y-%m-%d') AS aeEndDe, TS.MNTRNG_MEMO AS mntrngMemo, DATE_FORMAT(TS.MNTRNG_REGIST_DE, '%Y-%m-%d') AS mntrngRegistDe, DATE_FORMAT(TS.VISIT_RESVE_DE, '%Y-%m-%d') AS visitResveDe, TS.MNTRNG_DCSN_AT AS mntrngDcsnAt, TS.MNTRNG_DCSN_ESNTL_ID AS mntrngDcsnEsntlId, (SELECT CONCAT(TU.USER_NM, '(', TU.EMAIL_ADRES, ')') FROM COMTNUSERINFO TU WHERE TU.ESNTL_ID = TS.MNTRNG_DCSN_ESNTL_ID) AS mntrngDcsnUser, TS.SAE_AT AS saeAt, TS.SAE_DEATH AS saeDeath, TS.SAE_LIFE_THRT AS saeLifeThrt, TS.SAE_HSPTLZ_NEED AS saeHsptlzNeed, TS.SAE_CSMALFNC AS saeCsmalfnc, TS.SAE_CSDEFORM AS saeCsdeform, TS.SAE_ETC_SITTN AS saeEtcSittn, TS.CAUSALITY_CODE AS causalityCode, TS.PREDICT_AT AS predictAt, TS.AE_ACTION_CODE AS aeActionCode, TS.ETC_ACTION_CODE AS etcActionCode, DATE_FORMAT(TS.MEDCIN_STOP_DE, '%Y-%m-%d') AS medcinStopDe, TS.AE_RSLT_CODE AS aeRsltCode, DATE_FORMAT(TS.ACTION_DE, '%Y-%m-%d') AS actionDe, TS.ACTION_MEMO AS actionMemo, TS.ACTION_DCSN_AT AS actionDcsnAt, TS.ACTION_DCSN_ESNTL_ID AS actionDcsnEsntlId, TS.USE_YN AS useYn, (SELECT CONCAT(TU.USER_NM, '(', TU.EMAIL_ADRES, ')') FROM COMTNUSERINFO TU WHERE TU.ESNTL_ID = IFNULL(TS.ACTION_DCSN_ESNTL_ID, T3.CNFRMR_ESNTL_ID)) AS actionDcsnUser, (SELECT CONCAT(IF(IFNULL(TC.GRADE1, '-') = '-', '', CONCAT('1 ',TC.GRADE1, '\\n')), IF(IFNULL(TC.GRADE2, '-') = '-', '', CONCAT('2 ',TC.GRADE2, '\\n')) , IF(IFNULL(TC.GRADE3, '-') = '-', '', CONCAT('3 ',TC.GRADE3, '\\n')), IF(IFNULL(TC.GRADE4, '-') = '-', '', CONCAT('4 ',TC.GRADE4, '\\n')) , IF(IFNULL(TC.GRADE5, '-') = '-', '', CONCAT('5 ',TC.GRADE5))) AS gradeTitle FROM AEMSTUDY TS LEFT JOIN PCNCTCAE TC ON TS.CTCAE_VER = TC.CTCAE_VER WHERE TS.STUDY_ID = #{studyId} AND TC.MEDDRA_CODE = TS.MEDDRA_CODE) AS gradeDc FROM AEMSUMRY TS, (SELECT TP.CNFRMR_ESNTL_ID FROM AEMSTDYPRTCPNT TP WHERE TP.STUDY_ID = #{studyId} AND TP.PRTCPNT_ID = #{prtcpntId}) T3 WHERE TS.STUDY_ID = #{studyId} AND TS.PRTCPNT_ID = #{prtcpntId}
AND TS.SUMRY_SEQ = (SELECT MAX(TG.AE_NO) FROM AEMSUMRY TG WHERE TG.STUDY_ID = #{studyId} AND TG.PRTCPNT_ID = #{prtcpntId})
AND TS.SUMRY_SEQ = #{sumrySeq}

mapper.update:


        UPDATE AEMPRTCPNTSYMPTMS SET
            SYMPTMS_CONFIRM = IFNULL(#{symptmsConfirm}, 'N')
            ,SYMPTMS_CN = IFNULL(#{symptmsCn}, '')
            ,CALL_PARTNER = IFNULL(#{callPartner}, '')
            ,LAST_UPDT_PNTTM = NOW()
		    ,LAST_UPDUSR_ID = #{lastUpdusrId}
        WHERE STUDY_ID = #{studyId}
            AND PRTCPNT_ID = #{prtcpntId}
            AND SYMPTMS_SEQ = #{symptmsSeq}

mapper.delete:


DELETE FROM AEMPRTCPNTSYMPTMS
WHERE STUDY_ID = #{studyId}
  AND PRTCPNT_ID = #{prtcpntId}
  AND SYMPTMS_SEQ = #{symptmsSeq}

mapper.select:
SELECT TPS.STUDY_ID AS studyId, TPS.PRTCPNT_ID AS prtcpntId, TPS.SYMPTMS_SEQ AS symptmsSeq, TPS.SYMPTMS_REGTYPE_CODE AS symptmsRegtypeCode, CASE WHEN TPS.SYMPTMS_REGTYPE_CODE ='300' THEN '전화' WHEN TPS.SYMPTMS_REGTYPE_CODE ='400' THEN '대면' ELSE '앱' END AS symRegTxt, DATE_FORMAT(TPS.SYMPTMS_REGIST_DE, '%Y%m%d') AS symptmsRegistDe, TPS.SYMPTMS_TITLE AS symptmsTitle, CONCAT_WS('~',DATE_FORMAT(TPS.SYMPTMS_STRTDE, '%Y-%m-%d'), IFNULL(DATE_FORMAT(TPS.SYMPTMS_ENDDE, '%Y-%m-%d'),'')) AS symptmsDe, (SELECT TU.USER_NM FROM COMTNUSERINFO TU WHERE TU.ESNTL_ID = TPS.FRST_REGISTER_ID) AS registerNm, IF(IFNULL(T1.CNT,0) > 0, '1', '0') AS useAt, IF(IFNULL(T2.CNT,0) > 0, 'Y', 'N') AS otherAEUseAt, IFNULL(IF( LENGTH(SYMPTMS_CN) > 10, CONCAT(LEFT(SYMPTMS_CN, 10),'...'), SYMPTMS_CN), '') AS symptmsCn, SYMPTMS_CONFIRM AS symptmsConfirm FROM AEMPRTCPNTSYMPTMS TPS LEFT JOIN ( SELECT TMS.STUDY_ID, TMS.PRTCPNT_ID, TMS.SYMPTMS_SEQ, COUNT(*) AS CNT FROM AEMMNTRNGSYMPTMS TMS WHERE 1=1
AND TMS.SUMRY_SEQ = #{sumrySeq}
AND TMS.SUMRY_SEQ = #{mntrngSeq}
GROUP BY TMS.STUDY_ID, TMS.PRTCPNT_ID, TMS.SYMPTMS_SEQ ) T1 ON TPS.STUDY_ID = T1.STUDY_ID AND TPS.PRTCPNT_ID = T1.PRTCPNT_ID AND TPS.SYMPTMS_SEQ = T1.SYMPTMS_SEQ LEFT JOIN ( SELECT TMS.STUDY_ID, TMS.PRTCPNT_ID, TMS.SYMPTMS_SEQ, COUNT(*) AS CNT FROM AEMMNTRNGSYMPTMS TMS WHERE 1=1
AND TMS.SUMRY_SEQ != #{sumrySeq}
AND TMS.SUMRY_SEQ != #{mntrngSeq}
GROUP BY TMS.STUDY_ID, TMS.PRTCPNT_ID, TMS.SYMPTMS_SEQ ) T2 ON TPS.STUDY_ID = T2.STUDY_ID AND TPS.PRTCPNT_ID = T2.PRTCPNT_ID AND TPS.SYMPTMS_SEQ = T2.SYMPTMS_SEQ WHERE TPS.STUDY_ID = #{studyId} AND TPS.PRTCPNT_ID = #{prtcpntId}
AND TPS.SYMPTMS_REGTYPE_CODE IN
#{item}
AND IF(IFNULL(T1.CNT,0) > 0, 'Y', 'N') = IF(#{useIncludeAt} = '', 'N', #{useIncludeAt})
ORDER BY TPS.SYMPTMS_REGIST_DE DESC, TPS.SYMPTMS_SEQ DESC
